import React, { Component, PureComponent, createContext, lazy, Suspense, Fragment, memo, useState, useEffect, useContext, useMemo, useRef, useCallback } from 'react';
import './TodoList.css'
import * as actions from './actionCreators'


let idSeq = Date.now()
const LS_key = '_$get_'

function bindActionCreators(actions, dispatch) {
    const ret = {}
    for (let key in actions) {
        actions[key] = function (...args) {
            const eachAction = actions[key]
            dispatch(eachAction(...args))
        }
    }
    return ret
}

function Control(props) {
    // const { dispatch } = props
    console.log('control',props)
    const inputRef = useRef()
    const onSubmit = (e) => {
        e.preventDefault()

        const newText = inputRef.current.value.trim()
        if (newText.length === 0) {
            return;
        }

        // actions.addToDo({
        //     id: ++idSeq,
        //     text: newText,
        //     complete: false
        // })

        // dispatch(actions.createAdd({
        //     id: ++idSeq,
        //     text: newText,
        //     complete: false
        // }))
        // dispatch({
        //     type:'add',
        //     payload: {
        //         id: ++idSeq,
        //         text: newText,
        //         complete: false
        //     }
        // })
        inputRef.current.value = '';
    }

    return (
        <div className='control'>
            <h1>todos</h1>
            <form onSubmit={onSubmit}>
                <input
                    className='new-todo'
                    ref={inputRef}
                    type="text"
                    placeholder='What needs to be done'
                />
            </form>
        </div>
    );
}

function TodoItem(props) {
    const {
        todo: {
            id,
            text,
            complete
        },
        dispatch
    } = props
    
    
    const handleChange = () => {
        dispatch(actions.createToggle(id))
        // dispatch({
        //     type: 'toggle',
        //     payload:id
        // })
        // toggleTodo(id)
    }

    const handleClick = () => {
        dispatch(actions.createRemove(id))
        // dispatch({
        //     type: 'remove',
        //     payload:id
        // })
        // removeTodo(id)
    }
    return (
        <li className='todo-item'>
        <input
            type='checkbox'
            onChange={handleChange}
            checked={complete}
            ></input>
            <label className={complete ? 'complete' : ''}>{text}</label>
            <button onClick={handleClick}>&#xd7;</button>
    </li>
    )
}

function Todos(props) {
    const {dispatch,todos} = props
    return (
        <div>
            <ul className='todo_list'>
                {
                    todos.map(item => (
                        // <TodoItem todo={item} removeTodo={removeTodo} toggleTodo={toggleTodo}>
                        <TodoItem todo={item} dispatch={dispatch} key={item.id}>
                            {item.text}
                        </TodoItem>
                    ))
                }
            </ul>
        </div>
    )
}

function TodoList() {
    const [todos, setTodos] = useState([])
    
    const addTodo = useCallback((todo) => {
        setTodos(todos => [...todos, todo])
        dispatch({
            type: 'add',
            payload:todo
        })
    },[])
    const removeTodo = useCallback((id) => {
        setTodos(todos => todos.filter(todo => {
            return todo.id !== id
        }))
    },[])

    const toggleTodo = useCallback((id) => {
        setTodos(todos => todos.map(todo => {
            return todo.id === id
                ? {
                    ...todo,
                    complete: !todo.complete
                }
                : todo;
        }))
    }, [])

    const dispatch = useCallback((action) => {
        const { type, payload } = action
        switch (type) {
            case 'add':
                setTodos(todos => [...todos,payload])
                break;
            case 'set':
                setTodos(payload)
            case 'remove':
                setTodos(todos => todos.filter(todo => {
                    return todo.id !== action.payload
                }))
            case 'toggle':
                setTodos(todos => todos.map(todo => {
                    return todo.id === action.payload
                        ? {
                            ...todo,
                            complete: !todo.complete
                        }
                        : todo;
                }))
        }
    },[])

    useEffect(() => {
        const todos = JSON.parse(localStorage.getItem(LS_key || '[]'))
        console.log(todos);
        dispatch(actions.createSet(todos))
        // dispatch({
        //     type: 'set',
        //     payload:todos
        // })
        // setTodos(todos)
    },[])

    useEffect(() => {
        localStorage.setItem(LS_key, JSON.stringify(todos))
    },[todos])

    return (
        <div className='todo-list'>
            {/* <Control addTodo={addTodo}></Control> */}
            {/* <Control dispatch={dispatch}></Control> */}
            <Control {
                ...bindActionCreators({
                    actions
                },dispatch)
            }></Control>
            {/* <Todos todos={todos} removeTodo={removeTodo} toggleTodo={toggleTodo}></Todos> */}
            <Todos todos={todos} dispatch={dispatch}></Todos>
        </div>
    )
}

export default TodoList;
