import React, { Component,createContext }from 'react';


const BatteryContext = createContext()
const OnBool = createContext()

class Leaf extends Component{
  render() {
    return (
      <div>
        <BatteryContext.Consumer>

          {
            battery => (
              <OnBool.Consumer>
                {a => <h1>B:{battery}====S:{String(a)}</h1>}
              </OnBool.Consumer>
            )
            }

        </BatteryContext.Consumer>
      </div>
    )
  }
}


class Middle extends Component{
  render() {
    return (
      <div>middle
        <Leaf></Leaf>
      </div>
    )
  }
}

class App extends Component{
  state = {
    value: 59,
    onBool:false
  }
  render() {
    const { value, onBool } = this.state
    console.log(onBool);
    return (
      <BatteryContext.Provider value={this.state.value}>
        <OnBool.Provider value={onBool}>
          <button onClick={() => { this.setState({ value: value - 1 }) }}> -1</button>
          <button onClick={() => { this.setState({ onBool: !onBool }) }}> switch</button>
          </OnBool.Provider>
        <Middle></Middle>
      </BatteryContext.Provider>
    )
  }
}

export default App;
